import{_ as e,c as t,o as l,ae as o}from"./chunks/framework.Dh1jimFm.js";const g=JSON.parse('{"title":"Duelista","description":"","frontmatter":{"title":"Duelista"},"headers":[],"relativePath":"doc/classes/baskerville/duelista.md","filePath":"doc/classes/baskerville/duelista.md"}'),s={name:"doc/classes/baskerville/duelista.md"};function i(r,a,d,n,u,m){return l(),t("div",null,a[0]||(a[0]=[o('<h1 id="duelista" tabindex="-1">Duelista <a class="header-anchor" href="#duelista" aria-label="Permalink to &quot;Duelista&quot;">​</a></h1><h2 id="descricao" tabindex="-1">Descrição <a class="header-anchor" href="#descricao" aria-label="Permalink to &quot;Descrição&quot;">​</a></h2><p>O <strong>Duelista</strong> é um mestre no combate um-a-um, especializado em velocidade, precisão e elegância. Estes combatentes são conhecidos por sua destreza com armas leves e pelo uso de táticas refinadas para derrotar seus oponentes. Além de suas habilidades de combate, os Duelistas possuem um forte senso de honra e muitas vezes seguem códigos estritos de conduta em batalha.</p><h2 id="aviso" tabindex="-1">Aviso <a class="header-anchor" href="#aviso" aria-label="Permalink to &quot;Aviso&quot;">​</a></h2><p>O <strong>Duelista</strong> é uma classe focada totalmente em combate corpo a corpo. Ele pode empunhar duas armas médias ao mesmo tempo e pode rolar os ataques com <strong>Destreza</strong>.</p><h2 id="progressao-de-nivel" tabindex="-1">Progressão de Nível <a class="header-anchor" href="#progressao-de-nivel" aria-label="Permalink to &quot;Progressão de Nível&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Nível</th><th>Mana</th><th>Características</th></tr></thead><tbody><tr><td>4º</td><td>240</td><td>Quarta Escolha de Skills</td></tr><tr><td>5º</td><td>280</td><td>Quinta Escolha de Skills</td></tr><tr><td>6º</td><td>320</td><td>Sexta Escolha de Skills + Aumento de Atributo</td></tr><tr><td>7º</td><td>360</td><td>Sétima Escolha de Skills</td></tr><tr><td>8º</td><td>400</td><td>Oitava Escolha de Skills</td></tr><tr><td>9º</td><td>440</td><td>Nona Escolha de Skills + Aumento de Atributo</td></tr><tr><td>10º</td><td>480</td><td>Décima Escolha de Skills</td></tr></tbody></table><h2 id="habilidades-por-nivel" tabindex="-1">Habilidades por Nível <a class="header-anchor" href="#habilidades-por-nivel" aria-label="Permalink to &quot;Habilidades por Nível&quot;">​</a></h2><h3 id="skills-lvl-4" tabindex="-1">Skills LVL 4 <a class="header-anchor" href="#skills-lvl-4" aria-label="Permalink to &quot;Skills LVL 4&quot;">​</a></h3><ul><li><strong>Parada Elegante</strong> <em>(20 de Mana)</em><ul><li>Realiza uma defesa precisa, anulando ataques inimigos com um movimento grácil da lâmina.</li></ul></li><li><strong>Olho de Águia</strong> <em>(15 de Mana)</em><ul><li>Percebe detalhes sutis durante o combate, revelando fraquezas nos oponentes.</li></ul></li><li><strong>Saque Relâmpago</strong> <em>(20 de Mana)</em><ul><li>Realiza um ataque sem chance de reação, causando <strong>3d8</strong> de dano.</li></ul></li></ul><h3 id="skills-lvl-5" tabindex="-1">Skills LVL 5 <a class="header-anchor" href="#skills-lvl-5" aria-label="Permalink to &quot;Skills LVL 5&quot;">​</a></h3><ul><li><strong>Lâmina Sombria</strong> <em>(25 de Mana)</em><ul><li>Infunde a lâmina com sombras, causando <strong>2d8</strong> de dano adicional e aplicando um efeito debilitante.</li></ul></li><li><strong>Corte Triplo</strong> <em>(10 de Mana)</em><ul><li>Realiza <strong>3 ataques seguidos</strong>, cada um deles pode ser um acerto crítico.</li></ul></li><li><strong>Passo Etéreo</strong> <em>(25 de Mana)</em><ul><li>Torna-se temporariamente intangível (<strong>1 rodada</strong>), permitindo que ataques inimigos passem através do corpo.</li></ul></li></ul><h3 id="skills-lvl-6" tabindex="-1">Skills LVL 6 <a class="header-anchor" href="#skills-lvl-6" aria-label="Permalink to &quot;Skills LVL 6&quot;">​</a></h3><ul><li><strong>Desbalancear</strong> <em>(15 de Mana)</em><ul><li>Realiza um golpe causando <strong>2d12</strong> de dano, tendo a chance de derrubar o inimigo (<strong>Teste de ataque contra o inimigo</strong>).</li></ul></li><li><strong>Dupla Crescente</strong> <em>(30 de Mana)</em><ul><li>Executa um golpe de baixo para cima com extrema velocidade.</li></ul></li><li><strong>Instinto Ofensivo</strong> <em>(Passiva)</em><ul><li>Aumenta o ataque (<strong>+1d de dano por nível, contando a partir deste</strong>).</li></ul></li></ul><h3 id="skills-lvl-7" tabindex="-1">Skills LVL 7 <a class="header-anchor" href="#skills-lvl-7" aria-label="Permalink to &quot;Skills LVL 7&quot;">​</a></h3><ul><li><strong>Pele de Ferro</strong> <em>(Passiva)</em><ul><li>Aumenta a defesa com a força arcana, recebendo <strong>+7HP</strong> e <strong>+1DEF por nível</strong> (<strong>a partir deste nível</strong>).</li></ul></li><li><strong>Dança Nebulosa</strong> <em>(35 de Mana)</em><ul><li>Oculta suas intenções enquanto faz uma dança de lâminas, fazendo seu próximo ataque ser um <strong>acerto crítico</strong>.</li></ul></li><li><strong>Choque Arcano</strong> <em>(20 de Mana)</em><ul><li>Desfere uma joelhada no inimigo, <strong>atordoando-o por 1d3 rodadas</strong> e causando <strong>2d8</strong> de dano.</li></ul></li></ul>',16)]))}const h=e(s,[["render",i]]);export{g as __pageData,h as default};
